<?phprequire_once FILE_ACCESS_CORE_CODE.'/Framework/MVC_superClasses_Core/controllerSuperClass_Core/controllerSuperClass_Core.php';require_once('Model.php');require_once('View.php');class controller extends controllerSuperClass_Core{	public function indexAction()	{			$subpage = $this->getValueOfURLParameterPair("company");				if($subpage == NULL)			$page_id = "data";		else			$page_id = routes::getInstance()->getCurrentPageId();				$view = new view();		$model = new model();				$view->page_id = $page_id;		$view->_XMLObj = $model->loadDataSimpleXML($page_id);		$view->renderAll();		$view->displayMainTemplate();	}}?>