<?phprequire_once FILE_ACCESS_CORE_CODE.'/Framework/MVC_superClasses_Core/controllerSuperClass_Core/controllerSuperClass_Core.php';require_once('Model.php');require_once('View.php');require_once "Project/1_Website/Code/Modules/mailer.php";class controller extends controllerSuperClass_Core
{	public function indexAction()	{			$view = new view();		$model = new model();		$view->_XMLObj = $model->loadDataSimpleXML("data");		$view->renderAll();	}			public function sendEmailAction ()	{			$body = "							Name : {$_POST["name"]}<br />							Company : {$_POST["company"]} <br />							Telephone : {$_POST["telephone"]} <br />							Email : {$_POST["email"]} <br />							Message : {$_POST["message"]} <br />						";					$from = array(	"email" => $_POST['email'],						"name"  => $_POST['name']	);						mailer::send_with_phpmailer($from, 'Inquiry', $body);							header("Location: /contact?success=1");		}}?>