<?phprequire_once FILE_ACCESS_CORE_CODE.'/Framework/MVC_superClasses_Core/controllerSuperClass_Core/controllerSuperClass_Core.php';require_once('Model.php');require_once('View.php');require_once "Project/Model/Articles/articles/articles_query_helper.php";class controller extends controllerSuperClass_Core{	public function indexAction()	{			$articles_query_helper = new articles_query_helper();		$articles_query_helper->article_type = "hotels";		$articles_query_helper->is_published = true;		$hotels = $articles_query_helper->selectAllArticles();				$view = new view();		$view->hotels = $hotels;		$model = new model();		$view->_XMLObj = $model->loadDataSimpleXML("data");		$view->renderAll();	}		public function viewAction() 	{		$permalink = $this->getValueOfURLParameterPair("view");		$articles_query_helper = new articles_query_helper();		$hotel = $articles_query_helper->selectByColumn("r.permalink", $permalink);				$view = new view();		$view->hotel = $hotel;		$view->displayHotelTemplate();	}}?>