<?phprequire_once FILE_ACCESS_CORE_CODE.'/Framework/MVC_superClasses_Core/controllerSuperClass_Core/controllerSuperClass_Core.php';require_once('Model.php');require_once('View.php');require_once "Project/Model/Articles/articles/articles_query_helper.php";class controller extends controllerSuperClass_Core{	public function indexAction()	{			$articles_query_helper = new articles_query_helper();		$articles_query_helper->article_type = "members";		$articles_query_helper->is_published = true;		$articles_query_helper->category = array("permalink" => "full-members");		$members = $articles_query_helper->selectAllArticles();				$view = new view();		$view->members = $members;		$model = new model();						$subpage = $this->getValueOfURLParameterPair("members");				if($subpage == NULL)		$page_id = "data";		else		$page_id = routes::getInstance()->getCurrentPageId();				$view->_XMLObj = $model->loadDataSimpleXML($page_id);		$view->renderAll();	}		public function viewAction() 	{		$permalink = $this->getValueOfURLParameterPair("view");		$articles_query_helper = new articles_query_helper();		$member = $articles_query_helper->selectByColumn("r.permalink", $permalink);				$view = new view();		$view->member = $member;		$view->displayMemberTemplate();	}		public function adherentAction()	{				$articles_query_helper = new articles_query_helper();		$articles_query_helper->article_type = "members";		$articles_query_helper->is_published = true;		$articles_query_helper->category = array("permalink" => "adherent-members");		$members = $articles_query_helper->selectAllArticles();				$view = new view();		$view->members = $members;		$view->displayAdherentTemplate();	}}?>