<?phprequire_once FILE_ACCESS_CORE_CODE.'/Framework/MVC_superClasses_Core/viewSuperClass_Core/viewSuperClass_Core.php';class view extends viewSuperClass_Core{	public $members;	public $member;		public function __construct()	{		$current_page_id = routes::getInstance()->getCurrentTopLevelURLName();		$this->template_location = "Project/".DOMAIN."/Design/Pages/primary/members/templates/";	}		public function displayMemberTemplate()	{		$content = $this->renderTemplate($this->template_location."member.phtml");		response::getInstance()->addContentToTree(array('MAIN_CONTENT'=>$content));			$content = $this->renderTemplate($this->template_location."css_links.phtml");		response::getInstance()->addContentToStack("local_css", array("" => $content));				$content = $this->renderTemplate($this->template_location."inpage_javascript.js");		response::getInstance()->addContentToStack("local_javascript_bottom", array("" => $content) );	}		public function displayAdherentTemplate()	{		$content = $this->renderTemplate($this->template_location."adherent.phtml");		response::getInstance()->addContentToTree(array('MAIN_CONTENT'=>$content));			$content = $this->renderTemplate($this->template_location."css_links.phtml");		response::getInstance()->addContentToStack("local_css", array("" => $content));			$content = $this->renderTemplate($this->template_location."inpage_javascript.js");		response::getInstance()->addContentToStack("local_javascript_bottom", array("" => $content) );	}	
}