<?phprequire_once FILE_ACCESS_CORE_CODE.'/Framework/MVC_superClasses_Core/controllerSuperClass_Core/controllerSuperClass_Core.php';require_once('Model.php');require_once('View.php');require_once "Project/Model/Articles/articles/articles_query_helper.php";class controller extends controllerSuperClass_Core{	public function indexAction()	{			$articles_query_helper = new articles_query_helper();		$articles_query_helper->article_type = "press-room";		$articles_query_helper->is_published = true;		$press_room = $articles_query_helper->selectAllArticles();				$view = new view();		$view->press_room = $press_room;		$model = new model();						$subpage = $this->getValueOfURLParameterPair("press-room");				if($subpage == NULL)		$page_id = "data";		else		$page_id = routes::getInstance()->getCurrentPageId();				$view->_XMLObj = $model->loadDataSimpleXML($page_id);		$view->renderAll();	}		public function viewAction() 	{		$permalink = $this->getValueOfURLParameterPair("view");		$articles_query_helper = new articles_query_helper();		$pres = $articles_query_helper->selectByColumn("r.permalink", $permalink);				$view = new view();		$view->pres = $pres;		$view->displayPressTemplate();	}		public function featuresAction()	{		$model = new model();		$view  = new view();		$view->_XMLObj = $model->loadDataSimpleXML("data");		$view->renderAll();		$view->displayFeaturesTemplate();			}}?>