<?phprequire_once FILE_ACCESS_CORE_CODE.'/Framework/MVC_superClasses_Core/viewSuperClass_Core/viewSuperClass_Core.php';class view extends viewSuperClass_Core{	public $press_room;	public $pres;		public function __construct()	{		$current_page_id = routes::getInstance()->getCurrentTopLevelURLName();		$this->template_location = "Project/".DOMAIN."/Design/Pages/primary/press_room/templates/";	}		public function displayPressTemplate()	{		$content = $this->renderTemplate($this->template_location."press.phtml");		response::getInstance()->addContentToTree(array('MAIN_CONTENT'=>$content));			$content = $this->renderTemplate($this->template_location."css_links.phtml");		response::getInstance()->addContentToStack("local_css", array("" => $content));				$content = $this->renderTemplate($this->template_location."inpage_javascript.js");		response::getInstance()->addContentToStack("local_javascript_bottom", array("" => $content) );	}		public function displayFeaturesTemplate()	{		$content = $this->renderTemplate($this->template_location."features.phtml");		response::getInstance()->addContentToTree(array('MAIN_CONTENT'=>$content));			$content = $this->renderTemplate($this->template_location."css_links.phtml");		response::getInstance()->addContentToStack("local_css", array("" => $content));				$content = $this->renderTemplate($this->template_location."inpage_javascript.js");		response::getInstance()->addContentToStack("local_javascript_bottom", array("" => $content) );	}}